import {
  Button,
  Card,
  CardBody,
  Col,
  Container,
  Form,
  FormGroup,
  Input,
  Label,
  Row,
} from "reactstrap";
import { loadAllCategories } from "../services/category-service";
import { useEffect, useRef, useState } from "react";
import JoditEditor from "jodit-react";

const AddPost = () => {
  const editor = useRef(null);
  // const [content, setContent] = useState("");

  const [post, setPost] = useState({
    postTitle: "",
    postContent: "",
    categoryId: "",
  });

  const [categories, setCategories] = useState([]);

  useEffect(() => {
    loadAllCategories()
      .then((data) => {
        console.log(data);
        setCategories(data);
      })
      .catch((error) => {
        console.log(error);
      });
  }, []);

  // Field changed function
  const fieldChange = (event) => {
    console.log(event);
    setPost({ ...post, [event.target.name]: event.target.value });
  };

  // Content field change function
  const contentFieldChange = (data) => {
    setPost({ ...post, postContent: data });
  };

  //Reset all data..
  const resetData = () => {
    setPost({
      postTitle: "",
      postContent: "",
      categoryId: "",
    });
  };

  // Create Post Function..
  const createPost = () => {};

  return (
    <Row className="mt-5">
      <Col sm={{ size: 10, offset: 1 }}>
        <Card className="shadow">
          <CardBody>
            {JSON.stringify(post)}
            <div>
              <h3 className="text-center">Create Post</h3>
            </div>
            <Form onSubmit={createPost}>
              <FormGroup>
                <Label for="title">Post title</Label>
                <Input
                  type="text"
                  id="title"
                  placeholder="Enter title"
                  name="postTitle"
                  onChange={fieldChange}
                />
              </FormGroup>
              <FormGroup>
                <Label for="content">Post Content</Label>
                {/* <Input
                  type="textarea"
                  id="content"
                  style={{ height: "150px" }}
                /> */}
                <JoditEditor
                  ref={editor}
                  value={post.content}
                  onChange={contentFieldChange}
                />
              </FormGroup>
              <FormGroup>
                <Label for="category">Post category</Label>
                <Input
                  type="select"
                  id="category"
                  name="categoryId"
                  onChange={fieldChange}
                >
                  {categories.map((category) => (
                    <option
                      key={category.categoryId}
                      value={category.categoryId}
                    >
                      {category.categoryTitle}
                    </option>
                  ))}
                </Input>
              </FormGroup>
              <Container className="text-center">
                <Button type="submit" color="primary" className="shadow">
                  Create post
                </Button>
                <Button
                  onClick={resetData}
                  color="secondary"
                  className="ms-2 shadow"
                >
                  Reset
                </Button>
              </Container>
            </Form>
          </CardBody>
        </Card>
      </Col>
    </Row>
  );
};

export default AddPost;
