import React, { useEffect, useState } from "react";
import { getAllPost } from "../services/post_service";
import { error } from "./../../node_modules/jodit/esm/core/helpers/utils/error/error";
import {
  Container,
  Row,
  Col,
  Pagination,
  PaginationItem,
  PaginationLink,
} from "reactstrap";
import { size } from "./../../node_modules/jodit/esm/core/helpers/size/object-size";
import { offset } from "./../../node_modules/jodit/esm/core/helpers/size/offset";
import Post from "./Post";

const NewFeed = () => {
  const [postContent, setPostContent] = useState({
    content: [],
    pageNumber: "",
    pageSize: "",
    totalPages: "",
    pageSize: "",
    totalElements: "",
    lastPage: false,
  });

  useEffect(() => {
    // load all posts from server..
    getAllPost()
      .then((data) => {
        console.log(data);
        setPostContent(data);
      })
      .catch((error) => {
        console.log(error);
      });
  }, []);

  // Method required for Pagination..
  const changePage = (pageNumber=0, pageSize=5) => {
    getAllPost(pageNumber, pageSize).then(data=>{
        setPostContent(data);
    }).catch(error=>{
        toast.error("Error in loading posts !!")
    })
  };

  return (
    <Container>
      <Row className="mt-3">
        <Col
          md={{
            size: 10,
            offset: 1,
          }}
        >
          <h3 className="text-center">
            Blog Count : {postContent ? postContent.totalElements : 0}
          </h3>

          {postContent.content?.map((post) => (
            <Post key={post.postId} post={post} />
          ))}

          <Container className="center mt-3">
            <Pagination size="sm">
              <PaginationItem disabled={postContent.pageNumber == 0}>
                <PaginationLink previous></PaginationLink>
              </PaginationItem>
              {[...Array(postContent.totalPages)].map((item, index) => (
                <PaginationItem
                  active={index == postContent.pageNumber}
                  key={index}
                >
                  <PaginationLink>{index + 1}</PaginationLink>
                </PaginationItem>
              ))}
              <PaginationItem disabled={postContent.lastPage}>
                <PaginationLink next></PaginationLink>
              </PaginationItem>
            </Pagination>
          </Container>
        </Col>
      </Row>
    </Container>
  );
};

export default NewFeed;
