import { useEffect, useState } from "react";
import { signUp } from "../services/user-service";
import Base from "../components/Base";
import { toast } from "react-toastify";
import {
  Button,
  Card,
  CardBody,
  CardHeader,
  Col,
  Container,
  Form,
  FormFeedback,
  FormGroup,
  Input,
  Label,
  Row,
} from "reactstrap";

const Signup = () => {
  const [data, setData] = useState({
    name: "",
    email: "",
    password: "",
    about: "",
  });

  const [error, setError] = useState({
    errors: {},
    isError: false,
  });

  const [isValid, setIsValid] = useState({
    name: false,
    email: false,
    password: false,
    about: false,
  });

  // Additional part to print data on console..
  //   useEffect(() => {
  //     console.log(data);
  //   }, [data]);

  //Handle Change
  const handleChange = (event, property) => {
    //Dynamically setting the values..
    setData({ ...data, [property]: event.target.value });

    // Simple validation check
    if (event.target.value.trim() !== "") {
      setIsValid((prev) => ({ ...prev, [property]: true }));
      setError((prev) => ({
        ...prev,
        errors: { ...prev.errors, [property]: null },
      }));
    } else {
      setIsValid((prev) => ({ ...prev, [property]: false }));
    }
  };

  // Reset form
  const resetData = () => {
    setData({
      name: "",
      email: "",
      password: "",
      about: "",
    });

    setError({ errors: {}, isError: false });
    setIsValid({
      name: false,
      email: false,
      password: false,
      about: false,
    });
  };

  //Submit form
  const submitForm = (event) => {
    event.preventDefault();

    // This is client side validation
    // if(error.isError){
    // 	toast.error("Form data is invalid !!")

    // 	return;
    // }

    console.log(data);

    //data validation

    //call server api for sending data..
    signUp(data)
      .then((resp) => {
        console.log(resp);
        console.log("success log");
        toast.success("Registration Successfull !!");
        setData({
          name: "",
          email: "",
          password: "",
          about: "",
        });
      })
      .catch((error) => {
        console.log(error);
        console.log("error log");

        // Handling errors
        setError({
          errors: error,
          isError: true,
        });
      });
  };

  // Automatically clear validation messages after 3 seconds
  useEffect(() => {
    if (Object.values(isValid).some((val) => val) || error.isError) {
      const timer = setTimeout(() => {
        setIsValid({
          name: false,
          email: false,
          password: false,
          about: false,
        });
        setError({ errors: {}, isError: false });
      }, 3000);

      return () => clearTimeout(timer);
    }
  }, [isValid, error]);

  return (
    <Base>
      <Container>
        {/* {JSON.stringify(data)} */}
        <Row className="mt-5">
          <Col sm={{ size: 6, offset: 3 }}>
            <Card color="dark" outline>
              <CardHeader className="text-center">
                <h3>Register</h3>
              </CardHeader>
              <CardBody>
                {/* Creating form */}
                <Form onSubmit={submitForm}>
                  {/* Name field */}
                  <FormGroup>
                    <Label form="name">Enter name</Label>
                    <Input
                      type="text"
                      id="name"
                      placeholder="Enter name"
                      onChange={(e) => handleChange(e, "name")}
                      value={data.name}
                      invalid={
                        error.errors?.response?.data?.name ? true : false
                      }
                      valid={data.name && !error.errors?.response?.data?.name}
                    ></Input>
                    <FormFeedback>
                      {error.errors?.response?.data?.name}
                    </FormFeedback>
                  </FormGroup>
                  {/* Email field */}
                  <FormGroup>
                    <Label form="email">Enter email</Label>
                    <Input
                      type="email"
                      id="email"
                      placeholder="Enter email"
                      onChange={(e) => handleChange(e, "email")}
                      value={data.email}
                      invalid={
                        error.errors?.response?.data?.email ? true : false
                      }
                    ></Input>
                    <FormFeedback>
                      {error.errors?.response?.data?.email}
                    </FormFeedback>
                  </FormGroup>
                  {/* Password field */}
                  <FormGroup>
                    <Label form="password">Enter password</Label>
                    <Input
                      type="password"
                      id="password"
                      placeholder="Enter password"
                      onChange={(e) => handleChange(e, "password")}
                      value={data.password}
                      invalid={
                        error.errors?.response?.data?.password ? true : false
                      }
                    ></Input>
                    <FormFeedback>
                      {error.errors?.response?.data?.password}
                    </FormFeedback>
                  </FormGroup>
                  {/* About field */}
                  <FormGroup>
                    <Label form="about">About</Label>
                    <Input
                      type="textarea"
                      id="about"
                      placeholder="Write something here.."
                      style={{ height: "150px" }}
                      onChange={(e) => handleChange(e, "about")}
                      value={data.about}
                      invalid={
                        error.errors?.response?.data?.about ? true : false
                      }
                    ></Input>
                    <FormFeedback>
                      {error.errors?.response?.data?.about}
                    </FormFeedback>
                  </FormGroup>
                  <Container className="text-center">
                    <Button color="primary">Register</Button>
                    <Button
                      onClick={resetData}
                      color="secondary"
                      type="reset"
                      className="ms-2"
                    >
                      Reset
                    </Button>
                  </Container>
                </Form>
              </CardBody>
            </Card>
          </Col>
        </Row>
      </Container>
    </Base>
  );
};

export default Signup;
