import userEvent from "@testing-library/user-event";
import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import { Card, CardBody, CardText, Container } from "reactstrap";
import { getCurrentUserDetail } from "../auth";

function Post({
  post = {
    postTitle: "This is default post title",
    postContent: "This is default post content",
  },
}) {
  const [user, setUser] = useState(null);

  useEffect(() => {
    setUser(getCurrentUserDetail());
  });

  return (
    <Card className="shadow mt-3">
      <CardBody>
        <h4>{post.postTitle}</h4>
        <CardText
          dangerouslySetInnerHTML={{
            __html: post.postContent.substring(0, 100) + "......",
          }}
        ></CardText>
        <Container className="text-end">
          <Link
            className="btn btn-secondary btn-sm shadow"
            to={"/posts/" + post.postId}
          >
            Read More
          </Link>
        </Container>
      </CardBody>
    </Card>
  );
}

export default Post;
