import { Link, useParams } from "react-router-dom";
import Base from "../components/Base";
import { Card, CardBody, CardText, Col, Container, Row } from "reactstrap";
import { useEffect, useState } from "react";
import { getPostById } from "../services/post_service";
import { toast } from "react-toastify";
import { BASE_URL } from "../services/helper";

const PostPage = () => {
  const { postId } = useParams();
  const [post, setPost] = useState({});

  useEffect(() => {
    // Load post by postId
    getPostById(postId)
      .then((data) => {
        console.log(data);
        setPost(data);
      })
      .catch((error) => {
        console.log(error);
        toast.error("Error in loading post !!");
      });
  }, []);

  const printDate = (numbers) => {
    return new Date(numbers).toLocaleString();
  };

  return (
    <Base>
      <Container className="mt-4">
        <Row>
          <Col
            md={{
              size: 10,
              offset: 1,
            }}
          >
            <Link style={{ color:'black'}} to="/">Home</Link> / <u>{post.postTitle}</u>
            <Card className="mt-2 shadow ps-2 pe-2 pt-2"> 
              {post && (
                <CardBody>
                  <CardText>
                    <h3> {post.postTitle}</h3>
                  </CardText>
                  <div
                    className="image-container mt-3"
                    style={{ maxWidth: "50%" }}
                  >
                    <img
                      className="img-fluid"
                      src={BASE_URL + "/api/post/image/" + post.imageName}
                      alt=""
                      style={
                        {
                          // height: "100%",
                          // width: "100%",
                          // objectFit: "cover",
                        }
                      }
                    />
                  </div>
                  <CardText
                    dangerouslySetInnerHTML={{ __html: post.postContent }}
                    className="mt-3"
                  >
                  </CardText>
                  <div className="d-flex justify-content-between mt-4">
                    <CardText className="text-start text-muted">
                      <small>
                        <i> Category: {post.category?.categoryTitle}</i>
                      </small>
                    </CardText>
                    <CardText className="text-end text-muted">
                      <small>
                        <i>
                          Posted by <b>{post.user?.name} </b> on{" "}
                          {printDate(post.postDate)}
                        </i>
                      </small>
                    </CardText>
                  </div>
                </CardBody>
              )}
            </Card>
          </Col>
        </Row>
        <Row>
          <Col md={
            {
              size : 9,
              offset : 1
            }
          }>
            <h3>Comments</h3>
            {
              post.comments?.comment && post.comments?.comment.map(c=>(
                <h2>{c.comments?.comment}</h2>
              ))
            }
          </Col>
        </Row>
      </Container>
    </Base>
  );
};

export default PostPage;
