import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import { Button, Card, CardBody, CardText, Container } from "reactstrap";
import { getCurrentUserDetail, isLoggedIn } from "../auth";
import {deletePost} from "../pages/user-routes/Userdashboard";

function Post({
  post = {
    id: -1,
    postTitle: "This is default post title",
    postContent: "This is default post content",
    deletePost,
  },
}) {
  const [user, setUser] = useState(null);
  const [login, setLogin] = useState(null);

  useEffect(() => {
    setUser(getCurrentUserDetail());
    setLogin(isLoggedIn());
  }, []);

  return (
    <Card className="shadow mt-3">
      <CardBody>
        <h4>{post.postTitle}</h4>
        <CardText
          dangerouslySetInnerHTML={{
            __html: post.postContent.substring(0, 100) + "......",
          }}
        ></CardText>
        <Container className="text-end">
          <div>
            <Link
              className="btn btn-secondary btn-sm shadow"
              to={"/posts/" + post.postId}
            >
              Read More
            </Link>
            {isLoggedIn &&
              (user && user.id == post.user.id ? (
                <Button color="danger" className="ms-2" size="sm" onClick={()=>deletePost(post)}>
                  Delete
                </Button>
              ) : (
                " "
              ))}
          </div>
        </Container>
      </CardBody>
    </Card>
  );
}

export default Post;
