import { useEffect, useState } from "react";
import { NavLink as ReactLink } from "react-router-dom";
import { isLoggedIn, getCurrentUserDetail } from "../auth";
import {
  Collapse,
  Navbar,
  NavbarToggler,
  NavbarBrand,
  Nav,
  NavItem,
  NavLink,
  UncontrolledDropdown,
  DropdownToggle,
  DropdownMenu,
  DropdownItem,
} from "reactstrap";

const CustomNavbar = () => {
  const [isOpen, setIsOpen] = useState(false);

  // State for login status and user details
  const [login, setLogin] = useState(false);
  const [user, setUser] = useState(undefined);

  //   useEffect(() => {
  //     setLogin(isLoggedIn());
  //     setUserDetail(getCurrentUserDetail());
  //   }, [login]);
  // Fetch login state and user details when component mounts
  //   useEffect(() => {
  //     setLogin(isLoggedIn());
  //     if (isLoggedIn()) {
  //       setUser(getCurrentUserDetail());
  //     }
  //   }, []);
  useEffect(() => {
    setLogin(isLoggedIn());
    setUser(getCurrentUserDetail());

    const handleStorageChange = () => {
      setLogin(isLoggedIn());
      setUser(getCurrentUserDetail());
    };

    window.addEventListener("storage", handleStorageChange);
    return () => window.removeEventListener("storage", handleStorageChange);
  }, [login]);

  return (
    <div>
      <Navbar color="dark" dark expand="md" fixed="" className="px-5">
        <NavbarBrand tag={ReactLink} to="/">
          MyBlogs
        </NavbarBrand>
        <NavbarToggler onClick={() => setIsOpen(!isOpen)} />
        <Collapse isOpen={isOpen} navbar>
          <Nav className="me-auto" navbar>
            <NavItem>
              <NavLink tag={ReactLink} to="/">
                Home
              </NavLink>
            </NavItem>
            <NavItem>
              <NavLink tag={ReactLink} to="/about">
                About
              </NavLink>
            </NavItem>
            <NavItem>
              <NavLink tag={ReactLink} to="/services">
                Services
              </NavLink>
            </NavItem>

            <UncontrolledDropdown nav inNavbar>
              <DropdownToggle nav caret>
                More
              </DropdownToggle>
              <DropdownMenu>
                <DropdownItem>Contact Us</DropdownItem>
                <DropdownItem divider />
                <DropdownItem>Reset</DropdownItem>
              </DropdownMenu>
            </UncontrolledDropdown>
          </Nav>
          <Nav navbar>
            {login ? (
              <>
                <NavItem>
                  <NavLink>Logout</NavLink>
                </NavItem>
                <NavItem>
                  <NavLink>{user?.email}</NavLink>
                </NavItem>
              </>
            ) : (
              <>
                <NavItem>
                  <NavLink tag={ReactLink} to="/login">
                    Login
                  </NavLink>
                </NavItem>
                <NavItem>
                  <NavLink tag={ReactLink} to="/signup">
                    Signup
                  </NavLink>
                </NavItem>
              </>
            )}
          </Nav>
        </Collapse>
      </Navbar>
    </div>
  );
};

export default CustomNavbar;
