import { Link, useParams } from "react-router-dom";
import Base from "../components/Base";
import { Card, CardBody, CardText, Col, Container, Row } from "reactstrap";
import { useEffect, useState } from "react";
import { getPostById } from "../services/post_service";
import { toast } from "react-toastify";
import { BASE_URL } from "../services/helper";

const PostPage = () => {
  const { postId } = useParams();
  const [post, setPost] = useState({});

  useEffect(() => {
    // Load post by postId
    getPostById(postId)
      .then((data) => {
        console.log(data);
        setPost(data);
      })
      .catch((error) => {
        console.log(error);
        toast.error("Error in loading post !!");
      });
  }, []);

  const printDate = (numbers) => {
    return new Date(numbers).toLocaleString();
  };

  return (
    <Base>
      <Container className="mt-4">
        <Link to="/">Home</Link>
        <Row>
          <Col
            md={{
              size: 10,
              offset:1
            }}
          >
            <Card className="mt-3 shadow">
              {post && (
                <CardBody className="p-4">
                  <CardText>
                    <h3> {post.postTitle}</h3>
                  </CardText>
                  <div
                    className="image-container mt-3"
                    style={{ width:'100%'}}
                  >
                    <img
                      className="img-fluid"
                      src={BASE_URL + "/api/post/image/" + post.imageName}
                      alt=""
                      style={{
                        height: "100%",
                        width: "100%",
                        objectFit: "cover",
                      }}
                    />
                  </div>
                  <CardText className="mt-3">
                    <p>{post.postContent}</p>
                  </CardText>
                  <CardText className="text-end">
                    <small>
                      Posted by <b>{post.user?.name} </b> on{" "}
                      {printDate(post.postDate)}
                    </small>
                  </CardText>
                </CardBody>
              )}
            </Card>
          </Col>
        </Row>
      </Container>
    </Base>
  );
};

export default PostPage;
