import {
  Button,
  Card,
  CardBody,
  Col,
  Container,
  Form,
  FormGroup,
  Input,
  Label,
  Row,
} from "reactstrap";
import { loadAllCategories } from "../services/category-service";
import { useEffect, useState } from "react";

const AddPost = () => {
  const [categories, setCategories] = useState([]);

  useEffect(() => {
    loadAllCategories()
      .then((data) => {
        console.log(data);
        setCategories(data);
      })
      .catch((error) => {
        console.log(error);
      });
  }, []);

  return (
    <Row className="mt-5">
      <Col sm={{ size: 8, offset: 2 }}>
        <Card className="shadow">
          <CardBody>
            <div>
              <h3 className="text-center">Create Post</h3>
            </div>
            <Form>
              <FormGroup>
                <Label for="title">Post title</Label>
                <Input type="text" id="title" placeholder="Enter title" />
              </FormGroup>
              <FormGroup>
                <Label for="content">Post Content</Label>
                <Input
                  type="textarea"
                  id="content"
                  style={{ height: "150px" }}
                />
              </FormGroup>
              <FormGroup>
                <Label for="category">Post category</Label>
                <Input type="select" id="category">
                  {categories.map((category) => {
                    <Option>{category.categoryTitle}</Option>;
                  })}
                </Input>
              </FormGroup>
              <Container className="text-center">
                <Button color="primary" className="shadow">
                  Create post
                </Button>
                <Button color="secondary" className="ms-2 shadow">
                  Reset
                </Button>
              </Container>
            </Form>
          </CardBody>
        </Card>
      </Col>
    </Row>
  );
};

export default AddPost;
